SELECT `client_info`.`id_cli`, `client_info`.`cli_name`, `client_info`.`cli_invoice`, `client_info`.`cont_name`, `client_info`.`cont_phone`, `time`.`call_time`, `time`.`passed_time`, `time`.`end_call_time`, `que_ans`.`que`, `que_ans`.`ans`, `que_ans`.`admin` FROM `client_info` LEFT JOIN `time` ON `client_info`.`id_cli` = `time`.`id_cli` LEFT JOIN `que_ans` ON `time`.`id_call` = `que_ans`.`id_time` ORDER By `id_call` DESC